
import { createClient } from 'https://esm.sh/@supabase/supabase-js@2.38.4'

// Define CORS headers
const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
}

// Create Supabase client with service role key to bypass RLS
const supabaseUrl = Deno.env.get('SUPABASE_URL') || '';
const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY') || '';
const supabase = createClient(supabaseUrl, supabaseServiceKey);

// Main function to handle requests
Deno.serve(async (req) => {
  // Handle CORS preflight requests
  if (req.method === 'OPTIONS') {
    return new Response(null, { headers: corsHeaders });
  }

  try {
    const body = await req.json();
    const { action, pitchDeckId, pitchDeckData, generatePdf = false, token } = body;
    
    // Get auth token from request headers or body
    let authToken = req.headers.get('Authorization');
    
    if (authToken) {
      authToken = authToken.replace('Bearer ', '');
      console.log("Using Authorization header token");
    } else if (token) {
      authToken = token;
      console.log("Using token from request body");
    }
    
    // Check if token is present
    let userAuthRequired = true;
    let userId = null;
    
    if (authToken) {
      // Get user from token
      try {
        const { data: { user }, error: userError } = await supabase.auth.getUser(authToken);
        
        if (!userError && user) {
          userId = user.id;
          console.log("User authenticated:", userId);
        } else {
          console.log("Token provided but user auth failed:", userError);
        }
      } catch (authErr) {
        console.log("Auth error when getting user:", authErr);
      }
    } else {
      console.log("No auth token provided");
    }
    
    // For public access to get-pdf action
    if (action === 'get-pdf' && pitchDeckId && !userId) {
      userAuthRequired = false;
    }
    
    // For actions that require authentication
    if (userAuthRequired && !userId) {
      return new Response(
        JSON.stringify({ success: false, error: 'Authentication required' }),
        { 
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
          status: 401 
        }
      );
    }
    
    let responseData;
    
    // Handle different actions
    switch (action) {
      case 'save': {
        if (!pitchDeckData) {
          throw new Error('No pitch deck data provided');
        }
        
        const { title, description, mortgage_data, template_type, client_info, loan_officer_info } = pitchDeckData;
        let updateData = {
          title,
          description,
          mortgage_data,
          template_type,
          updated_at: new Date().toISOString()
        };
        
        // If we are updating an existing pitch deck
        if (pitchDeckId) {
          console.log(`Updating pitch deck ${pitchDeckId}`);
          
          // First check if the pitch deck exists and user has permission
          const { data: existingDeck, error: checkError } = await supabase
            .from('pitch_decks')
            .select('id, created_by')
            .eq('id', pitchDeckId)
            .single();
          
          if (checkError) {
            console.error('Check error:', checkError);
            throw new Error(`Pitch deck not found: ${checkError.message}`);
          }
          
          if (userId && existingDeck.created_by !== userId) {
            throw new Error('You do not have permission to update this pitch deck');
          }
          
          // Update the pitch deck
          const { data, error } = await supabase
            .from('pitch_decks')
            .update(updateData)
            .eq('id', pitchDeckId)
            .select()
            .single();
          
          if (error) {
            console.error('Update error:', error);
            throw new Error(`Failed to update pitch deck: ${error.message}`);
          }
          
          responseData = {
            success: true,
            data
          };
        } else {
          // Create a new pitch deck
          console.log('Creating new pitch deck');
          
          if (!userId) {
            throw new Error('User ID is required to create a pitch deck');
          }
          
          const { data, error } = await supabase
            .from('pitch_decks')
            .insert({
              ...updateData,
              created_by: userId
            })
            .select()
            .single();
          
          if (error) {
            console.error('Insert error:', error);
            throw new Error(`Failed to create pitch deck: ${error.message}`);
          }
          
          responseData = {
            success: true,
            data
          };
        }
        break;
      }
      
      case 'get-pdf': {
        if (!pitchDeckId) {
          throw new Error('Pitch deck ID is required');
        }
        
        // Fetch the pitch deck data
        const { data: pitchDeck, error: fetchError } = await supabase
          .from('pitch_decks')
          .select('*')
          .eq('id', pitchDeckId)
          .single();
        
        if (fetchError || !pitchDeck) {
          console.error('Fetch error:', fetchError);
          throw new Error(`Failed to fetch pitch deck: ${fetchError?.message || 'Not found'}`);
        }
        
        // Simple PDF generation (base64 encoded) 
        // In a real application, you would use a proper PDF generation library
        const dummyPdfData = `data:application/pdf;base64,`;
        
        responseData = {
          success: true,
          pdfData: dummyPdfData
        };
        break;
      }
      
      default:
        throw new Error(`Unknown action: ${action}`);
    }
    
    return new Response(
      JSON.stringify(responseData),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 200,
      }
    );
  } catch (error) {
    console.error(`Error in save-pitch-deck function: ${error.message}`);
    
    return new Response(
      JSON.stringify({ success: false, error: error.message }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 400,
      }
    );
  }
});
